DROP TABLE `bewerbung`;
CREATE TABLE `bewerbung` (  `Bewerbung_ID` int(10) unsigned NOT NULL AUTO_INCREMENT,  `Charname` varchar(20) NOT NULL,  `Rasse` varchar(60) NOT NULL,  `Klasse` varchar(60) NOT NULL,  `Ausrichtung` varchar(60) NOT NULL,  `Warum` varchar(10000) NOT NULL,  `Vorlieben` varchar(60) NOT NULL,  `Zeit` char(5) NOT NULL,  `Zusatz` varchar(10000) NOT NULL,  `Mo` char(6) DEFAULT NULL,  `Di` char(8) DEFAULT NULL,  `Mi` char(8) DEFAULT NULL,  `Do` char(10) DEFAULT NULL,  `Fr` char(7) DEFAULT NULL,  `Sa` char(7) DEFAULT NULL,  `So` char(7) DEFAULT NULL,  `JT` char(9) DEFAULT NULL,  `alter` varchar(20) NOT NULL DEFAULT 'ohne Angabe',  `Aufgenommen` tinyint(1) NOT NULL DEFAULT '0',  `Bearbeitung` tinyint(1) NOT NULL DEFAULT '0',  `Grund` varchar(1000) NOT NULL,  PRIMARY KEY (`Bewerbung_ID`)) ENGINE=InnoDB AUTO_INCREMENT=24 DEFAULT CHARSET=utf8;
DROP TABLE `chars`;
CREATE TABLE `chars` (  `ID` int(11) NOT NULL AUTO_INCREMENT,  `User_ID` int(11) NOT NULL,  `Char` varchar(30) NOT NULL,  `Klasse` varchar(5) NOT NULL,  PRIMARY KEY (`ID`),  UNIQUE KEY `Char` (`Char`)) ENGINE=InnoDB AUTO_INCREMENT=52 DEFAULT CHARSET=utf8;
DROP TABLE `counter`;
CREATE TABLE `counter` (  `cid` int(10) NOT NULL AUTO_INCREMENT,  `ip` varchar(15) NOT NULL,  `timestamp` int(10) NOT NULL,  PRIMARY KEY (`cid`)) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;
DROP TABLE `mediapics`;
CREATE TABLE `mediapics` (  `PIC_ID` int(10) unsigned NOT NULL AUTO_INCREMENT,  `PIC_GROSS` varchar(160) NOT NULL,  `PIC_KLEIN` varchar(160) NOT NULL,  `PICDatum` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,  `PIC_Beschreibung` varchar(28) NOT NULL,  PRIMARY KEY (`PIC_ID`)) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8;
DROP TABLE `mediavideo`;
CREATE TABLE `mediavideo` (  `Video_ID` int(10) unsigned NOT NULL AUTO_INCREMENT,  `PfadVideo` varchar(160) NOT NULL,  `VideoDatum` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,  PRIMARY KEY (`Video_ID`)) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;
DROP TABLE `news`;
CREATE TABLE `news` (  `NewsID` int(10) unsigned NOT NULL AUTO_INCREMENT,  `Titel` varchar(50) NOT NULL,  `News` varchar(1000) NOT NULL,  `Bild` varchar(160) NOT NULL,  PRIMARY KEY (`NewsID`)) ENGINE=InnoDB AUTO_INCREMENT=56 DEFAULT CHARSET=utf8;
DROP TABLE `raidplaner`;
CREATE TABLE `raidplaner` (  `ID` int(11) NOT NULL AUTO_INCREMENT,  `Raid` varchar(20) CHARACTER SET utf8 NOT NULL,  `Modus` varchar(2) CHARACTER SET utf8 NOT NULL,  `Tag` int(11) NOT NULL,  `Monat` int(11) NOT NULL,  `Jahr` int(11) NOT NULL,  `Uhrzeit` varchar(5) CHARACTER SET utf8 NOT NULL,  `User_ID` int(11) NOT NULL,  `Raidleiter` varchar(20) CHARACTER SET utf8 NOT NULL,  `Gruppe` varchar(20) CHARACTER SET utf8 NOT NULL,  PRIMARY KEY (`ID`)) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=latin1;
DROP TABLE `raids`;
CREATE TABLE `raids` (  `ID` int(11) NOT NULL AUTO_INCREMENT,  `Raid` varchar(30) CHARACTER SET utf8 NOT NULL,  `geschafft` int(11) NOT NULL,  `gesamt` int(11) NOT NULL,  PRIMARY KEY (`ID`)) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=latin1;
DROP TABLE `raidteilnahme`;
CREATE TABLE `raidteilnahme` (  `ID` int(11) NOT NULL AUTO_INCREMENT,  `RaidID` int(11) NOT NULL,  `CharID` int(11) NOT NULL,  `Teilnahme` int(11) NOT NULL,  `UserID` int(11) NOT NULL,  `Kommentar` varchar(101) NOT NULL,  PRIMARY KEY (`ID`),  UNIQUE KEY `ID` (`ID`)) ENGINE=InnoDB AUTO_INCREMENT=111 DEFAULT CHARSET=utf8;
DROP TABLE `site_counter`;
CREATE TABLE `site_counter` (  `ip` varchar(15) NOT NULL,  `visit` date NOT NULL,  `online` varchar(255) NOT NULL) ENGINE=MyISAM DEFAULT CHARSET=latin1;
DROP TABLE `user`;
CREATE TABLE `user` (  `User_ID` int(10) unsigned NOT NULL AUTO_INCREMENT,  `Email` varchar(100) NOT NULL,  `Passwort` char(32) NOT NULL,  `Mainchar` varchar(20) NOT NULL,  `RLBildklein` varchar(160) DEFAULT NULL,  `RLBildgross` varchar(160) DEFAULT NULL,  `Vorname` varchar(60) DEFAULT NULL,  `Name` varchar(60) DEFAULT NULL,  `GebDatum` date DEFAULT NULL,  `Ort` varchar(60) DEFAULT NULL,  `Telefon` varchar(25) DEFAULT NULL,  `oeffentlEmail` tinyint(1) DEFAULT NULL,  `Beschreibung` varchar(1000) DEFAULT NULL,  `Status` enum('aktiv','admin','deaktiviert','neu') DEFAULT 'neu',  `Online` bigint(20) NOT NULL,  PRIMARY KEY (`User_ID`),  UNIQUE KEY `Mainchar` (`Mainchar`)) ENGINE=InnoDB AUTO_INCREMENT=52 DEFAULT CHARSET=utf8;
