INSERT INTO `bewerbung` (`Bewerbung_ID`, `Charname`, `Rasse`, `Klasse`, `Ausrichtung`, `Warum`, `Vorlieben`, `Zeit`, `Zusatz`, `Mo`, `Di`, `Mi`, `Do`, `Fr`, `Sa`, `So`, `JT`, `alter`, `Aufgenommen`, `Bearbeitung`, `Grund`) VALUES
(10, 'umu', 'Mensch', 'Sith-Inquisitor', 'Heal', 'Suche Aktive Raidgilde um weiter zukommen', 'PVE', '15:00', 'Habe Wechselschicht und muss jeder Woche umfahren aber wenn ich weis wann ein raid ist bin ich da wenn ich mich angemeldet habe', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'jt', 'ohne Angabe', 0, 1, ''),
(11, 'Tuckitucki', 'Mensch', 'Sith-Krieger', 'Tank', 'suche eine aktive Raid Gilde. Da in der Gilde, wo ich jetzt ', 'PVE', '11:00', 'hab nen knall seid nachsichtig^^', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'jt', 'ohne Angabe', 0, 1, ''),
(13, 'Nêxx', 'Mensch', 'Sith-Krieger', 'DDmelee', 'Hi Imperial March,<br />\r\n<br />\r\nhiermit bewerbe ich mich (', 'PVE', '16:00', 'Hier würde ich uns gerne noch einmal vorstellen: Wir sind 19-20 Jahre alt und kommen aus Niedersachsen. Da wir oft zusammen zocken, war es für uns eigentlich klar dass wir gemeinsam einer Gilde beitreten wollen.<br />\r\n<br />\r\nSpieler:<br />\r\n<br />\r\nTim 19 Jahre, Ingame: Nêxx, Marauder<br />\r\nMarcel 19 Jahre, Ingame: Râyy, Sorcerer<br />\r\nDaniel 20 Jahre, Ingame: [Pending], Imperialer Agent (subclass unentschlossen)', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'jt', '19', 1, 1, ''),
(14, 'Roheyn', 'Sith', 'Sith-Krieger', 'Tank', 'Eigentlich hatte ich nach vielen erfolgreichen Jahren WOW-Ga', 'PVE', '22:00', 'Zu Onlinezeiten:<br />\nStändig wechselnd aufgrund Schichtdienst und Familienleben. Kernzeiten: 8-16 oder 22 bis 02 Uhr<br />\n<br />\n', 'Mo', 'Di', 'Mi', 'Do', 'Fr', 'Sa', 'So', NULL, '35', 1, 1, ''),
(16, 'Khrain', 'Mensch', 'Imperialer-Agent', 'DDrange', 'Da ich in das Raidgeschehen wieder mit einsteigen möchte , bin ich auf der Suche nach einer aktiven Raidgilde, welche auch ein schönes Heim für Schichtarbeiter und motivierte Raider bietet.', 'PVE', '15:00', 'Hey Zusammen,<br />\r\n<br />\r\nMein Name ist Daniel, bin 23 Jahre alt und komme aus dem schönen Wien in Österreich.<br />\r\nLeider muss ich schon vorweg sagen, dass ich Schichtarbeiter am Flughafen Wien-Schwechat bin und somit nicht zu allen Raidzeiten online sein kann, was ich aber durch jahrelange MMORPG und Raiderfahrung (WOW seit 2004 - Swtor seit 2012) in Sachen Gameplay und Taktik wieder wettmache.<br />\r\nWeiters bin ich sehr in American Football und Fußball interessiert und habe auch selbst 2 Jahre / 10 Jahre gespielt.<br />\r\n<br />\r\n•Informationen über meinen Hauptcharakter<br />\r\n<br />\r\nOrientierung: PvE<br />\r\n<br />\r\nName: Khrain<br />\r\n<br />\r\nSpezialisierung: MM ( je nachdem was am meisten Spaß macht und wo ich den größtmöglichen Schaden für den Raid erzielen kann)<br />\r\n<br />\r\nRolle : DD<br />\r\n<br />\r\nSpielzeit: wie gesagt, leider Schichtarbeiter ( ich gebe aber rechtzeitig bescheid sollte ein Termin nicht gehen!)<br />\r\n<br />\r\nEquip: Jau hier kommen wir zum Knackpunkt ', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'jt', '24', 1, 1, ''),
(17, 'Ezzriel', 'Sith', 'Sith-Inquisitor', 'DDmelee', 'Weil ich verrückt bin und ich grad nicht weiß, ob ich halbnackte tanzende Kerle nicht epischen Fights vorziehen würde. Kurzum: ich bin auf der Suche nach Entscheidungshilfen. <br />\r\n<br />\r\nIch habe euer Gesuch im offiziellen Forum gesehen und bin dann hier gelandet. Die Mischung zwischen gemütlichem Feierabend-Zocken und Pole-Dan...ähhh, Nightmare-Raiden ist genau das, was ich suche.', 'PVE', '20:00', 'Auch wenn Ezzi erst zarte 23 ist, die lenkende Hand hinter ihr ist fast doppelt so alt, also 29 und irgendsoein komischer Buchstabe. Ich spiele seit Ewigkeiten, lange Zeit GW, dann kam HdRO, wo ich auch erfolgreich geraidet habe, bis die Swtor Beta kam. Die HdRO eindeutig den Rang abgelaufen hat. <br />\r\n<br />\r\nDie Leute, mit denen ich Swtor angefangen habe, haben leider alle aus unterschiedlichen Gründen aufgehört. Damit blieb ich bis jetzt auf imperialer Seite gildenlos (Rep. nur eine Bankgilde mit einer Freundin), was beim Leveln auch kein großes Problem ist.<br />\r\n<br />\r\nJetzt ist Ezzi aber vor kurzem groß geworden (ja guut, ich bin KEIN Powerlevler und ähhh ja, ich habe vielleicht noch ein-zwei andere kleine Chars so in jeder Klasse in jeder Fraktion), gerade dabei ihren Kleiderschrank aufzurüsten und sucht neue Herausforderungen. Da ich es nicht mag, mit Randoms zu spielen, war ich bislang noch in keiner OP und auch die meisten FPs sind noch offen. Da beißt sich auch die Katze', NULL, 'Di', 'Mi', 'Do', 'Fr', 'Sa', NULL, NULL, '23', 1, 1, ''),
(18, 'Maranjia', 'Sith', 'Sith-Inquisitor', 'DDrange', 'Ich suche eine nette kleine Feierabendgilde, mit der man Spaß haben kann, die aber keine großen Onlinezwänge hat, von den Raids mal abgesehen. Ihr scheint dies zu bieten. Zudem war ich vor ein paar Tagen mit einem von euch im Flashpoint und die Person war sehr nett und Hilsbereit, ich weiß nur leider den Namen grad nicht mehr...', 'PVE/PVP', '17:00', '', NULL, 'Di', 'Mi', 'Do', 'Fr', 'Sa', 'So', NULL, '23', 1, 1, ''),
(19, 'Seylol', 'Miraluka', 'Sith-Krieger', 'Tank', 'Ich suche eine Gilde dir mir hilft das Spiel zu erklären mit seinen ganzen Dingen zudem möchte ich auch nicht alleine spielen da ich eine Pause eingelegt hatte und nun in einer leeren Gilde bin suche ich nach einer die mir Hilft das Spiel wieder öfters und nicht alleine zu spielen :)', 'PVE', '17:00', 'Ich kann keine genauen Tage nennen da ich Arbeiten muss und mich auch um meine Freundin kümmern muss ^^ ', NULL, 'Di', 'Mi', 'Do', NULL, 'Sa', NULL, NULL, '22', 1, 1, ''),
(20, 'Giganthus', 'Sith', 'Sith-Krieger', 'DDmelee', 'Als Marauder suche ich gerne nach einer Herausforderung was das raiden betrifft, die beiden neuen kenn ich schon im sm und asa hc clear, a&v 6/7. Da sich meine Gilde (first class gaming) so langsam auflöst suche ich dementsprechend eine neue und habe eure Seite im Allgemeinchat gelesen. Ich hoffe ich kann euch tatkräftig unterstützen.', 'Casual', '21:00', 'Ich bin nicht jeden Tag online, da ich sehr oft bis 20 Uhr arbeiten muss.<br />\r\nzurzeit bin ich aber mein Sniper am hochspielen (bei Galadriêl ingame melden).', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'jt', '24', 1, 1, ''),
(21, 'Unus', 'Sith', 'Sith-Inquisitor', 'Heal', 'Das haben wir besprochen bei dem Proberaid gestern.', 'PVE', '20:00', 'Bin Schichtarbeiter, also varrieren meine Spielzeiten strak je nach Dienstplann und Familie.', NULL, 'Di', NULL, 'Do', 'Fr', 'Sa', 'So', NULL, '35', 1, 1, ''),
(22, 'Chabneela', 'Sith', 'Sith-Krieger', 'DDmelee', 'Hallo, Ihr macht schon mal einen netten Eindruck habe im Forum euren Aufruf gelesen und denke das könnte  eine gute Gemeinschaft sein wo ich mit rein passe!!!<br />\r\n<br />\r\nBin gut erzogen und habe reichlich Erfahrung durch mein noch junges Alter ^^<br />\r\n', 'PVE/PVP', '12:00', 'Spiele dieses Spiel seit 20 Tagen und es gefällt mir sehr die Geschichte finde ich gelungen. Bin in einer Gilde seid 4 Tagen nur irgendwie geht sie gerade auseinnander. Da ich kein Gilden Springer sein möchte! Wissen Sie das ich mich wo anders noch Bewerbe mag keinen auf irgend einer weise beschummel. Also somit habe ich kein Schlechtes gewissen. Freu mich auf ein Gespräch im TS3 bei euch LG', 'Mo', 'Di', 'Mi', 'Do', 'Fr', 'Sa', 'So', 'jt', '35', 0, 1, ''),
(23, 'Thedo', 'Mensch', 'Imperialer-Agent', 'DDrange', 'Hallo. Zur Zeit befinde ich mich als wiedereinsteiger in einer Gilde die aus ca 95 % Ex-Mitgliedern besteht, weil jeder von ihnen ein anderes Spielt in Anspruch genommen hat. Viele dieser Leute kannte ich aus anderen MMO''s (vorrangig WoW). Leider haben sehr viele keine Lust mehr auf SWTOR. Da mich das Spiel aber wieder reizt würde ich gerne in eine Gilde in der ein wenig was los ist. Momentan bin ich noch in der Level Phase und auf 52, aber ich bin guter dinge das ich es bald bis 55 geschafft habe. Eins muß ich aber noch los werden. Meine Zeit als aktiver Raidteilnehmer sind definitiv vorbei. Doch bin ich gerne bereit auszuhelfen wenn Not am Mann ist.', 'Casual', '09:00', 'Da ich Schicht arbeite und dadurch unterschiedliche Onlinezeiten habe, hab ich mal jeden Tag angegeben, was aber nicht heißen soll das ich jeden Tag da bin. Auch die Zeit sind auf Grund dessen recht unterschiedlich', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'jt', '33', 0, 1, '');
